value.value:
@if(
    not(empty(pipeline().parameters.p_data_particao)),
    substring(replace(pipeline().parameters.p_data_particao,'-','/'),0,10),
    if(
        equals(pipeline().parameters.p_tipo_carga,'F'),
        '1900/01/01',
        if(
            or(
                empty(pipeline().parameters.p_formato_data_particao),
                equals(pipeline().parameters.p_formato_data_particao, 'D')
            ),
            convertFromUtc(utcnow(), 'E. South America Standard Time', 'yyyy/MM/dd'),
            if(
                equals(pipeline().parameters.p_formato_data_particao, 'M'),
                convertFromUtc(utcnow(), 'E. South America Standard Time', 'yyyy/MM/dd'),
                if(
                    equals(pipeline().parameters.p_formato_data_particao, 'A'),
                    convertFromUtc(utcnow(), 'E. South America Standard Time', 'yyyy/MM/dd'),
                    if(
                        equals(pipeline().parameters.p_formato_data_particao, 'N'), 
                        '', 
                        convertFromUtc(utcnow(), 'E. South America Standard Time', 'yyyy/MM/dd')
                    )
                )
            )
        )
    )  
)

@toLower(concat(
    variables('v_diretorio_destino'),
    '/', 
    pipeline().parameters.p_colecao_origem,
    if(
        empty(variables('v_data_particao')),
        '',
        concat('/', variables('v_data_particao'))
    )
))
